import React, { useEffect, useState } from 'react';

const AdminDashboard = () => {
  const [tasks, setTasks] = useState([]);
  const [analytics, setAnalytics] = useState({
    pending: 0,
    scheduled: 0,
    completed: 0
  });

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await fetch('http://your-backend-api/tasks', {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });

        if (response.ok) {
          const data = await response.json();
          setTasks(data.tasks);
          setAnalytics({
            pending: data.analytics.pending,
            scheduled: data.analytics.scheduled,
            completed: data.analytics.completed
          });
        } else {
          throw new Error('Failed to fetch tasks');
        }
      } catch (err) {
        console.error(err);
      }
    };

    fetchTasks();
  }, []);

  return (
    <div className="p-6 bg-gray-800 min-h-screen">
      <h1 className="text-3xl font-bold text-gray-100 mb-6">Admin Dashboard</h1>
      <div className="mb-6">
        <h2 className="text-2xl font-bold text-gray-300">Analytics</h2>
        <div className="bg-gray-900 p-4 rounded-lg">
          <p className="text-gray-100">Total Pending Tasks: {analytics.pending}</p>
          <p className="text-gray-100">Total Scheduled Tasks: {analytics.scheduled}</p>
          <p className="text-gray-100">Total Completed Tasks: {analytics.completed}</p>
        </div>
      </div>
      <div>
        <h2 className="text-2xl font-bold text-gray-300">Tasks</h2>
        <table className="min-w-full bg-gray-900 rounded-lg">
          <thead>
            <tr>
              <th className="py-2 px-4 text-left text-gray-100">Task Name</th>
              <th className="py-2 px-4 text-left text-gray-100">Description</th>
              <th className="py-2 px-4 text-left text-gray-100">Status</th>
              <th className="py-2 px-4 text-left text-gray-100">Schedule</th>
              <th className="py-2 px-4 text-left text-gray-100">Assigned User</th>
              <th className="py-2 px-4 text-left text-gray-100">Assigned Email</th>
            </tr>
          </thead>
          <tbody>
            {tasks.map(task => (
              <tr key={task.id}>
                <td className="py-2 px-4 text-gray-100">{task.task_name}</td>
                <td className="py-2 px-4 text-gray-100">{task.task_description}</td>
                <td className="py-2 px-4 text-gray-100">{task.task_status}</td>
                <td className="py-2 px-4 text-gray-100">{task.task_schedule}</td>
                <td className="py-2 px-4 text-gray-100">{task.assigned_user_name}</td>
                <td className="py-2 px-4 text-gray-100">{task.assigned_task_email}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AdminDashboard;
