import React, { useState } from 'react';
import { FaUserCircle, FaArrowLeft } from 'react-icons/fa';
import { Link } from 'react-router-dom';

const UsersPage = () => {
  const [users, setUsers] = useState([
    { id: 1, name: 'User A', username: 'userA', email: 'usera@example.com', password: 'password123' },
    { id: 2, name: 'User B', username: 'userB', email: 'userb@example.com', password: 'password123' },
    { id: 3, name: 'User C', username: 'userC', email: 'userc@example.com', password: 'password123' },
  ]);
  const [newUser, setNewUser] = useState({ name: '', username: '', email: '', password: '' });
  const [modalOpen, setModalOpen] = useState(false);

  const handleDeleteUser = (userId) => {
    if (window.confirm('Are you sure you want to delete this user?')) {
      setUsers(users.filter(user => user.id !== userId));
    }
  };

  const handleDeleteAllUsers = () => {
    if (window.confirm('Are you sure you want to delete all users?')) {
      setUsers([]);
    }
  };

  const handleUpdateField = (userId, field, value) => {
    const updatedUsers = users.map(user =>
      user.id === userId ? { ...user, [field]: value } : user
    );
    setUsers(updatedUsers);
  };

  const handleCreateUser = () => {
    if (newUser.name && newUser.username && newUser.email && newUser.password) {
      setUsers([...users, { id: Date.now(), ...newUser }]);
      setNewUser({ name: '', username: '', email: '', password: '' });
      setModalOpen(false);
    } else {
      alert('Please fill in all fields');
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewUser({ ...newUser, [name]: value });
  };

  return (
    <div className="flex flex-col h-screen">
      {/* Header */}
      <header className="bg-gray-800 text-gray-100 p-4 flex justify-between items-center">
        <Link to="/admin-dashboard">
          <button className="text-gray-100 flex items-center">
            <FaArrowLeft size={24} />
            <span className="ml-2">Back</span>
          </button>
        </Link>
        {/* Admin Profile */}
        <div className="flex items-center">
          <FaUserCircle size={40} className="text-gray-400 mr-2" />
          <span className="text-gray-300">Admin</span>
        </div>
      </header>

      {/* Main Content */}
      <main className="flex-1 p-6">
        <div className="flex justify-between mb-4">
          {/* Delete All Button */}
          <button
            className="bg-red-600 text-white px-4 py-2 rounded"
            onClick={handleDeleteAllUsers}
          >
            Delete All
          </button>
        </div>

        {/* Users Table */}
        <div className="overflow-x-auto mb-20">
          <table className="min-w-full bg-gray-800 text-gray-100 border border-gray-600 text-sm">
            <thead>
              <tr className="w-full border-b border-gray-600">
                <th className="px-4 py-2 text-left">Name</th>
                <th className="px-4 py-2 text-left">Username</th>
                <th className="px-4 py-2 text-left">Email</th>
                <th className="px-4 py-2 text-left">Password</th>
                <th className="px-4 py-2 text-left">Actions</th>
              </tr>
            </thead>
            <tbody>
              {users.map(user => (
                <tr key={user.id} className="border-b border-gray-600">
                  <td className="px-4 py-2">{user.name}</td>
                  <td className="px-4 py-2">
                    <input
                      type="text"
                      value={user.username}
                      onChange={(e) => handleUpdateField(user.id, 'username', e.target.value)}
                      className="bg-gray-700 text-white rounded px-2 py-1"
                    />
                  </td>
                  <td className="px-4 py-2">
                    <input
                      type="email"
                      value={user.email}
                      onChange={(e) => handleUpdateField(user.id, 'email', e.target.value)}
                      className="bg-gray-700 text-white rounded px-2 py-1"
                    />
                  </td>
                  <td className="px-4 py-2">
                    <input
                      type="password"
                      value={user.password}
                      onChange={(e) => handleUpdateField(user.id, 'password', e.target.value)}
                      className="bg-gray-700 text-white rounded px-2 py-1"
                    />
                  </td>
                  <td className="px-4 py-2">
                    <button
                      className="bg-red-600 text-white px-4 py-2 rounded"
                      onClick={() => handleDeleteUser(user.id)}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* Add User Button */}
        <div className="fixed bottom-4 right-4">
          <button
            className="p-4 bg-blue-600 text-white rounded-full shadow-lg"
            onClick={() => setModalOpen(true)}
          >
            +
          </button>
        </div>

        {/* Create User Modal */}
        {modalOpen && (
          <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
            <div className="bg-gray-800 p-6 rounded w-full max-w-sm space-y-4">
              <h2 className="text-white text-xl font-bold mb-4">Create New User</h2>
              <input
                type="text"
                name="name"
                value={newUser.name}
                onChange={handleInputChange}
                placeholder="Name"
                className="w-full p-2 bg-gray-700 text-white rounded"
              />
              <input
                type="text"
                name="username"
                value={newUser.username}
                onChange={handleInputChange}
                placeholder="Username"
                className="w-full p-2 bg-gray-700 text-white rounded"
              />
              <input
                type="email"
                name="email"
                value={newUser.email}
                onChange={handleInputChange}
                placeholder="Email"
                className="w-full p-2 bg-gray-700 text-white rounded"
              />
              <input
                type="password"
                name="password"
                value={newUser.password}
                onChange={handleInputChange}
                placeholder="Password"
                className="w-full p-2 bg-gray-700 text-white rounded"
              />
              <div className="flex justify-between space-x-2">
                <button
                  className="w-1/2 bg-blue-600 text-white p-2 rounded"
                  onClick={handleCreateUser}
                >
                  Create
                </button>
                <button
                  className="w-1/2 bg-red-600 text-white p-2 rounded"
                  onClick={() => setModalOpen(false)}
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

export default UsersPage;
