import React, { useState, useEffect } from 'react';

// Sample user data (can be replaced with data from an API)
const sampleUsers = [
  { id: 1, name: 'John Doe', email: 'john@example.com' },
  { id: 2, name: 'Jane Smith', email: 'jane@example.com' },
  { id: 3, name: 'Bob Johnson', email: 'bob@example.com' }
];

const Users = () => {
  const [users, setUsers] = useState([]);

  // Fetch users from an API or use static data
  useEffect(() => {
    // Simulate an API call
    setUsers(sampleUsers);
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Users List</h1>
      <table className="min-w-full bg-gray-800 text-gray-100 border border-gray-600">
        <thead>
          <tr className="w-full border-b border-gray-600">
            <th className="px-6 py-3 text-left">ID</th>
            <th className="px-6 py-3 text-left">Name</th>
            <th className="px-6 py-3 text-left">Email</th>
          </tr>
        </thead>
        <tbody>
          {users.map(user => (
            <tr key={user.id} className="border-b border-gray-600">
              <td className="px-6 py-4">{user.id}</td>
              <td className="px-6 py-4">{user.name}</td>
              <td className="px-6 py-4">{user.email}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Users;
